mapper.select:
		SELECT SH.SHOP_MAX_RESERVATION - (SELECT NVL(SUM(RE.RESERVE_COUNT),0)
                                    FROM RESERVATION RE
                                    WHERE RE.SHOP_NO = SH.SHOP_NO
                                    AND RE.RESERVE_DATE = #{reserveDate}
                                    AND RE.RESERVE_TIME = #{reserveTime}) AS CNT        
		FROM SHOP SH WHERE SH.SHOP_NO = #{shopNo}
